import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

const products = [
  {code: 'P1000', name:'UGG Women Classic Short Boot', category: 'Clothing'},
  {code: 'P2000', name:'Dell Inspiron 15.6-Inch Laptop', category: 'Electronic'},
  {code: 'P3000', name:'San Francisco Bay Coffee Breakfast Blend', category: 'Food'}
]

const Product = ({product}) => {
  return(
    <li> {product.code} -- {product.name} -- {product.category} </li>
  )
}

const App = (props) => {
  const[products, setProducts] = useState(props.products)
  const[newProductCode, setNewProductCode] = useState('a new product code')
  const[newProductName, setNewProductName] = useState('a new product name')
  const[newProductCate, setNewProductCate] = useState('a new product category')
  const[showAll, setShowAll] = useState(true)

  const productToShow = showAll
  ? products
  : products.filter(product => product.category === 'Electronic')


  const addProduct = (event) => {
    event.preventDefault()
    console.log('add button clicked', event.target)
    const productObject = {
      code: newProductCode,
      name: newProductName, 
      category: newProductCate
    }

    setProducts(products.concat(productObject))
    setNewProductCode('a new product code')
    setNewProductName('a new product name')
    setNewProductCate('a new product category')
  }

  const handleNewProductCode = (event) => {
    console.log(event.target.value)
    setNewProductCode(event.target.value)
  }

  const handleNewProductName = (event) => {
    console.log(event.target.value)
    setNewProductName(event.target.value)
  }

  const handleNewProductCate = (event) => {
    console.log(event.target.value)
    setNewProductCate(event.target.value)
  }

  return(
    <div>
      <h3> My Favorite Products</h3>
      <ul>
        {productToShow.map((product, index) => 
          <Product key={index} product={product} />
        )}
      </ul>
      <form onSubmit={addProduct}>
        <input 
          value={newProductCode}
          onChange={handleNewProductCode}
        />
        <input 
          value={newProductName}
          onChange={handleNewProductName}
        />
        <input 
          value={newProductCate}
          onChange={handleNewProductCate}
        />

        <button type='submit'> Add New Product </button>
      </form>
    </div>
  )
}
ReactDOM.render(
  <React.StrictMode>
    <App products={products}/>
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
