import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

const App = () => {
  const[action, setAction] = useState({view: 0, like: 0, addToCart: 0, purchase: 0})

  const handleView = () => {
      const newAction = {
          view: action.view + 1,
          like: action.like,
          addToCart: action.addToCart,
          purchase: action.purchase
      }
      setAction(newAction)
  }

  const handleLike = () => {
    const newAction = {
        view: action.view,
        like: action.like + 1,
        addToCart: action.addToCart,
        purchase: action.purchase
    }
    setAction(newAction)
  }

  const handleCart = () => {
    const newAction = {
        ...action,
        addToCart: action.addToCart + 1,
    }
    setAction(newAction)
  }

  const handlePurchase = () => setAction({...action, purchase: action.purchase + 1})

  return (
    <div>
      <p> Number of View: {action.view}</p>
      <button onClick={handleView}> View </button>
      <p> Number of Like: {action.like}</p>
      <button onClick={handleLike}> Like </button>
      <p> Number of Add-to-Cart: {action.addToCart}</p>
      <button onClick={handleCart}> Add-to-Cart </button>
      <p> Number of Purchase: {action.purchase}</p>
      <button onClick={handlePurchase}> Purchase </button>
    </div>
  )
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
