import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

const App = () => {
  const[view, setView] = useState(0)
  const[like, setLike] = useState(0)
  const[cart, setCart] = useState(0)
  const[purchase, setPurchase] = useState(0)
  const[actions, setActions] = useState([])

  const handleView = () => {
    setView(view+1)
    setActions(actions.concat('View'))
  }

  const handleLike = () => {
    setLike(like+1)
    setActions(actions.concat('Like'))
  }

  const handleCart = () => {
    setCart(cart+1)
    setActions(actions.concat('Add-to-Cart'))
  }

  const handlePurchase = () => {
    setPurchase(purchase+1)
    setActions(actions.concat('Purchase'))
  }

  return (
    <div>
      <p> Number of View: {view}</p>
      <button onClick={handleView}> View </button>
      <p> Number of Like: {like}</p>
      <button onClick={handleLike}> Like </button>
      <p> Number of Add-to-Cart: {cart}</p>
      <button onClick={handleCart}> Add-to-Cart </button>
      <p> Number of Purchase: {purchase}</p>
      <button onClick={handlePurchase}> Purchase </button>
      <p>All actions: {actions.join(' ')}</p>
    </div>
  )
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
