import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

const App = () => {
  const[view, setView] = useState(0)

  // const handleViewIncrease = () => setView(view+1)
  // const handleViewDecrease = () => setView(view-1)
  // const handleViewReset = () => setView(0)

  const handleView = (newValue) => {
    setView(newValue)
  }

  return (
    <div>
      <p> Number of View: {view} </p>
      <button onClick={handleView(view+1)}> Increase </button>
      <button onClick={handleView(view-1)}> Decrease </button>
      <button onClick={handleView(0)}> Reset </button>
    </div>
  )
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

