import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

const History = (props) => {
  if (props.actions.length === 0) {
    return(
      <div>
        <p>There is no any action</p>
      </div>
    )
  }
  return(
    <div>
      <p> All actions: {props.actions.join(' ')} </p>
    </div>
  )
}

const Button = ({eventHandler, text}) => {
  return(
    <button onClick={eventHandler}> {text}</button>
  )
}

const Display = ({text, action}) => {
  return(
    <p> Number of {text}: {action}</p>
  )
}

const App = () => {
  const[view, setView] = useState(0)


  const handleViewIncrease = () => setView(view+1)
  const handleViewDecrease = () => setView(view-1)
  const handleViewReset = () => setView(0)

  return (
    <div>
      <p> Number of View: {view} </p>
      <button onClick={handleViewIncrease}> Increase </button>
      <button onClick={handleViewDecrease}> Decrease </button>
      <button onClick={handleViewReset}> Reset </button>
    </div>
  )
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
