import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

const products = [
  {code: 'P1000', name:'UGG Women Classic Short Boot', category: ['Clothing', 'Shoes']},
  {code: 'P2000', name:'Dell Inspiron 15.6-Inch Laptop', category: ['Electronic']},
  {code: 'P3000', name:'San Francisco Bay Coffee Breakfast Blend', category: ['Food']}
]

const Product = ({product}) => {
  return(
    <li> {product.code} -- {product.name} -- {product.category.join('&')} </li>
  )
}

const App = () => {
  return(
    <div>
      <h3> My Favorite Products</h3>

      {/* the first way */}
      {/* <ul>
        <li>{products[0].code} -- {products[0].name} -- {products[0].category.join('&')}</li>
        <li>{products[1].code} -- {products[1].name} -- {products[1].category.join('&')}</li>
        <li>{products[2].code} -- {products[2].name} -- {products[2].category.join('&')}</li>
      </ul> */}

      {/* the second way */}
      {/* <ul>
        {products.map((product, index) => 
          <li key={index}> {product.code} -- {product.name} -- {product.category.join('&')} </li>
        )}
      </ul> */}

      {/* the third way */}
      <ul>
        {products.map((product, index) => 
          <Product key={index} product={product} />
        )}
      </ul>

    </div>
  )
}
ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

