import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

const App = () => {
  const[view, setView] = useState(0)
  const[like, setLike] = useState(0)
  const[addToCart, setAddToCart] = useState(0)
  const[purchase, setPurchase] = useState(0)

  return (
    <div>
      <p> Number of View: {view}</p>
      <button onClick={() => setView(view+1)}> View </button>
      <p> Number of Like: {like}</p>
      <button onClick={() => setLike(like+1)}> Like </button>
      <p> Number of Add-to-Cart: {addToCart}</p>
      <button onClick={() => setAddToCart(addToCart+1)}> Add-to-Cart </button>
      <p> Number of Purchase: {purchase}</p>
      <button onClick={() => setPurchase(purchase+1)}> Purchase </button>
    </div>
  )
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
