import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

const History = (props) => {
  if (props.actions.length === 0) {
    return(
      <div>
        <p>There is no any action</p>
      </div>
    )
  }
  return(
    <div>
      <p> All actions: {props.actions.join(' ')} </p>
    </div>
  )
}

const Button = ({eventHandler, text}) => {
  return(
    <button onClick={eventHandler}> {text}</button>
  )
}

const Display = ({text, action}) => {
  return(
    <p> Number of {text}: {action}</p>
  )
}

const App = () => {
  const[view, setView] = useState(0)
  const[like, setLike] = useState(0)
  const[cart, setCart] = useState(0)
  const[purchase, setPurchase] = useState(0)
  const[actions, setActions] = useState([])

  const handleView = () => {
    setView(view+1)
    setActions(actions.concat('View'))
  }

  const handleLike = () => {
    setLike(like+1)
    setActions(actions.concat('Like'))
  }

  const handleCart = () => {
    setCart(cart+1)
    setActions(actions.concat('Add-to-Cart'))
  }

  const handlePurchase = () => {
    setPurchase(purchase+1)
    setActions(actions.concat('Purchase'))
  }

  return (
    <div>
      <Display text='View' action={view}/> 
      <Button eventHandler={handleView} text='View'/> 
      <Display text='Like' action={like} />
      <Button eventHandler={handleLike} text='Like'/>
      <Display text='Add2Cart' action={cart}/> 
      <Button eventHandler={handleCart} text='Add2Cart'/>
      <Display text='Purchase' action={purchase}/> 
      <Button eventHandler={handlePurchase} text='Purchase' />
      <History actions={actions}/>
    </div>
  )
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
