import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';

const units = [
  {code: 'COMP1010', title:'Fundamentals of Computer Science', offering: ['S1', 'S2']},
  {code: 'COMP1750', title:'Introduction to Business Information Systems', offering: ['S1']},
  {code: 'COMP2110', title:'Web Technology', offering: ['S1', 'S2']},
  {code: 'COMP2750', title:'Applications Modelling and Development', offering: ['S1']},
  {code: 'MMCC2045', title:'Interactive Web Design', offering: ['S2']},
  {code: 'COMP3120', title:'Advanced Web Development', offering: ['S2']},
  {code: 'COMP3130', title:'Mobile Application Development', offering: ['S1']}
]

const Unit = ({unit, index}) => {
  return(
    <li> {unit.code} -- {unit.title} -- {unit.offering} </li>
  )
}

const App = (props) => {
  const[unit, setUnits] = useState(props.units)
  const[newUnitCode, setNewUnitCode] = useState('a new unit code')

  const addUnitCode = (event) => {
    event.preventDefault()
    console.log('add button clicked', event.target)
  }

  const handleNewProductCode = (event) => {
    console.log(event.target.value)
    setNewUnitCode(event.target.value)
  }
  return(
    <div>
      <h3> My Favorite Products</h3>
      <ul>
        {units.map((unit, index) => 
          <Unit unit={unit} key={index} />
        )}
      </ul>
      <form onSubmit={addUnitCode}>
        <input 
          value={newUnitCode}
          onChange={handleNewProductCode}
        />
        <button type='submit'> Add New Unit </button>
      </form>
    </div>
  )
}
ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
